mapper.update:
update `staking` set `staking_locked` = #{staking.stakingLocked}, `staking_hes` = #{staking.stakingHes}, `staking_reduction` = #{staking.stakingReduction}, `status` = #{staking.status}, `staking_reward_value` = `staking_reward_value` + #{staking.feeRewardValue}, `is_settle` = #{staking.isSettle}, `annualized_rate` = #{staking.annualizedRate}, `annualized_rate_info` = #{staking.annualizedRateInfo} where `node_id` = #{staking.nodeId} and staking_block_num = #{staking.stakingBlockNum};
update `node` set `staking_locked` = #{staking.stakingLocked}, `staking_hes` = #{staking.stakingHes}, `staking_reduction` = #{staking.stakingReduction}, `status` = #{staking.status}, `stat_staking_reward_value` = `stat_staking_reward_value` + #{staking.feeRewardValue}, `is_settle` = #{staking.isSettle}, `annualized_rate` = #{staking.annualizedRate}, `annualized_rate_info` = #{staking.annualizedRateInfo} where `node_id` = #{staking.nodeId};
update `delegation` set `delegate_hes` = 0, `delegate_locked` = `delegate_hes` + `delegate_locked` where `is_history` = 2 and `delegate_hes` > 0;
update `staking` set `stat_delegate_hes` = 0, `stat_delegate_locked` = `stat_delegate_hes` + `stat_delegate_locked` where `stat_delegate_hes` > 0;
