update.foreach:
        	/*1、更新提案*/
			update proposal set 

          	`yeas` = `yeas`- 1


          	`nays` = `nays`- 1


          	`abstentions` = `abstentions`- 1
        	 where `hash` = #{item.hash};
            /*2、更新投票*/
		    update vote set `option` = 4 where `hash` = #{item.voteHash} ;
