mapper.select:
select 

from block
where hash = #{hash,jdbcType=VARCHAR}
  and `number` = #{number,jdbcType=BIGINT}
  and chain_id = #{chainId,jdbcType=VARCHAR}

mapper.delete:
delete from block
where hash = #{hash,jdbcType=VARCHAR}
  and `number` = #{number,jdbcType=BIGINT}
  and chain_id = #{chainId,jdbcType=VARCHAR}
